<?php	include("database.php");	if($_SERVER["REQUEST_METHOD"] == "POST")	{	$myusername=addslashes($_POST['email']); 										// email sent from form 	$mypassword=addslashes($_POST['pass']); 										// password sent from form 	$sql="SELECT * FROM tbl_registration WHERE email='$myusername' and password='$mypassword'";		$result=mysql_query($sql);	$count=mysql_num_rows($result);	if($count==1)																	// If result matched $myusername and $mypassword, table row must be 1 row	{	header("location: comment_insert.php");	}		else 	{	echo"<script>alert('Your email or Password is invalid')</script>";	include('user_log_in.php');	}	}?>