<?php	$host = "localhost";	$user="root";	$pass = "";	$db = "Culture_BD";	$mysqli = new mysqli($host,$user,$pass,$db);							/*establish db connection*/	if (mysqli_connect_errno())												/* Check connection*/	{													echo "Failed to connect to MySQL: " . mysqli_connect_error();	}	$email=trim($_POST['email']);	$password=trim($_POST['pass']);		if($email == Null || $password == Null)	{		echo"<script>alert('please fill up both Email and PassWord !!')</script>";		include('user_registration.php');	}		else	{		$sql="INSERT INTO tbl_registration(email,password) VALUES ('$email','$password')";				$stmt=$mysqli->prepare($sql);		if($stmt->execute())		{			echo"<script>alert('Registered Successfully !!')</script>";			include('user_registration.php');		}		else		{			echo"<script>alert('Already Registered !!')</script>";			include('user_registration.php');		}	}	mysqli_close($mysqli);	?>